import React from 'react';
import {Text, View, Image, TextInput} from 'react-native';

export default class Login extends React.Component{

    render(){
        const {navigate} = this.props.navigation
        return(
            <View style={{backgroundColor:'#FFF', height:'100%'}}>
                <Image 
                    style = {{width:100, height:100, alignSelf:'center', marginTop:30}}
                    source = {require('../images/logo.jpg')}
                />

                <Text
                    style = {{
                        alignSelf:"center"
                    }}
                >E-Absen</Text>
                
                <Text
                    style = {{
                        alignSelf:"center"
                    }}
                >PKU Gamping</Text>

                <View style = {{
                    flexDirection:"row",
                    alignItem:"center",
                    marginHorizontal:55,
                    borderWidth:2,
                    marginTop:50,
                    paddingHorizontal:10,
                    borderColor:"#00716F",
                    borderRadius:23
                }}>
                <TextInput
                    placeholder="NIK"
                    placeholderTextColor="#00716F"
                    style={{paddingHorizontal:10}}/>
                </View>

                <View style = {{
                    flexDirection:"row",
                    alignItem:"center",
                    marginHorizontal:55,
                    borderWidth:2,
                    marginTop:10,
                    paddingHorizontal:10,
                    borderColor:"#00716F",
                    borderRadius:23
                }}>
                <TextInput
                    secureTextEntry={true}
                    placeholder="Password"
                    placeholderTextColor="#00716F"
                    style={{paddingHorizontal:10}}/>  
                </View>

                <View 
                
                style={{
                    marginHorizontal:55,
                    alignItems:"center",
                    justifyContent:"center",
                    marginTop:30,
                    backgroundColor:"#00716F",
                    paddingVertical:8,
                    borderRadius:23
                }}>
                    <Text 
                    
                    onPress={()=>navigate('Home')}
                    
                    style={{
                        color:"white"
                    }}>Log In</Text>
                </View>

                <Text 
                
                onPress={()=>navigate('Register')}
                
                style={{
                    alignSelf:"center",
                    color:"#00716F",
                    paddingVertical:30
                }}>Register</Text>
            </View>
        )
    }
}
